<?phpdeclare(strict_types=1);namespace Fugue\HTTP\Routing;use Fugue\Collection\ArrayMap;use InvalidArgumentException;use function is_string;final class RouteMap extends ArrayMap{    protected function checkKey($key): void    {        if (! is_string($key)) {            throw new InvalidArgumentException('Key must be a string');        }    }    protected function checkValue($value): bool    {        return $value instanceof Route;    }}